// Extract cases from Firecrawl with data-vw button attributes and WordPress nonce
const md = $json?.data?.markdown || "";
const html = $json?.data?.html || "";

// Extract WordPress nonce for AJAX calls
const nonceMatch = html.match(/ajax_nonce["'\s:]+["']([a-f0-9]+)["']/i);
const wpNonce = nonceMatch ? nonceMatch[1] : null;

// Function to extract data-vw value from button for a specific cause
function getButtonDataVw(cause) {
  if (!cause) return null;
  const idx = html.indexOf(cause);
  if (idx === -1) return null;

  const start = html.lastIndexOf("<tr", idx);
  const end = html.indexOf("</tr>", idx);
  if (start === -1 || end === -1) return null;

  const row = html.slice(start, end + 5);

  // Extract data-vw attribute from View button
  const vwMatch = row.match(/data-vw=\"([^\"]+)\"/i);
  return vwMatch ? vwMatch[1] : null;
}

// Parse markdown table rows
const lines = md.split("\n").filter(l => l.startsWith("|"));
if (lines.length < 2) return [];

const dataLines = lines.filter(l => !l.includes("---")).slice(1);

// Build case objects with data-vw codes and nonce
const cases = dataLines.map(row => {
  const cols = row.split("|").map(c => c.trim());
  const causeNumber = cols[1] || "";
  const filingDate = cols[2] || "";
  const title = cols[3] || "";
  const subject = cols[4] || "";
  const register = cols[5] || "";
  const dataVw = getButtonDataVw(causeNumber);

  return { causeNumber, filingDate, title, subject, register, dataVw, wpNonce };
});

// Filter for Financial Services + Petitions
let petitions = cases.filter(c => {
  const subject = (c.subject || "").toLowerCase();
  const register = (c.register || "").toLowerCase();

  const isFinancialServices = register.includes("financial") && !register.includes("family");
  const isTargetPetition = subject.includes("winding up") || subject.includes("petition");

  return isFinancialServices && isTargetPetition && c.dataVw && c.wpNonce;
});

// Sort by priority: Winding Up petitions first
petitions.sort((a, b) => {
  const aIsWindingUp = (a.subject || "").toLowerCase().includes("winding up");
  const bIsWindingUp = (b.subject || "").toLowerCase().includes("winding up");
  if (aIsWindingUp && !bIsWindingUp) return -1;
  if (!aIsWindingUp && bIsWindingUp) return 1;
  return 0;
});

const now = new Date().toISOString();
return petitions.map(c => ({ json: { ...c, scrapedAt: now } }));

// Filter for new cases only
const scraped = $items("Parse & Filter Petitions").map(i => i.json);
const existingRows = $items("Read Existing Cases").map(i => i.json).filter(Boolean);

// Build a set of existing case numbers
const seen = new Set(
  existingRows
    .map(r => String(r.causeNumber || "").trim().toUpperCase())
    .filter(Boolean)
);

// Keep only truly new cases
const fresh = scraped.filter(c => {
  const key = String(c.causeNumber || "").trim().toUpperCase();
  return key && !seen.has(key);
});

// If no existing data, pass everything through
const out = (fresh.length === 0 && seen.size === 0) ? scraped : fresh;

return out.map(c => ({ json: c }));

// Extract Box.com download URL from WordPress AJAX response
const caseData = $json;
const response = $prevNode.json;

if (!response.success || !response.data) {
  throw new Error(`WordPress AJAX failed for case ${caseData.causeNumber}: ${JSON.stringify(response)}`);
}

const boxFileId = response.data.fid;
const token = response.data.t;

// Construct Box.com download URL  
const downloadUrl = `https://api.box.com/2.0/files/${boxFileId}/content?access_token=${token}`;

return [{
  json: {
    ...caseData,
    pdfUrl: downloadUrl,
    boxFileId: boxFileId,
    extractedAt: new Date().toISOString()
  }
}];

// Convert PDF to base64 for Vision API
const pdfBuffer = $binary.data;
const base64Pdf = pdfBuffer.toString('base64');

const caseData = $prevNode.json;

return [{
  binary: {
    data: pdfBuffer,
    fileName: `${caseData.causeNumber}.pdf`,
    mimeType: 'application/pdf'
  },
  json: {
    ...caseData,
    base64Pdf: base64Pdf,
    pdfSize: pdfBuffer.length
  }
}];

// Parse GPT-4o response and structure the data
const caseData = $prevNode.json;
const aiResponse = $json;

// Try to parse JSON response from GPT-4o
let extractedData;
try {
  // GPT-4o might return JSON in a code block or wrapped in text
  const jsonMatch = aiResponse.choices[0].message.content.match(/```json\s*([\s\S]*?)\s*```/);
  if (jsonMatch) {
    extractedData = JSON.parse(jsonMatch[1]);
  } else {
    // Try parsing the entire response as JSON
    extractedData = JSON.parse(aiResponse.choices[0].message.content);
  }
} catch (error) {
  console.log('Failed to parse GPT response as JSON:', aiResponse.choices[0].message.content);
  extractedData = {
    partyDetails: 'Error parsing AI response',
    timeline: 'Error parsing AI response',
    financialStatus: 'Error parsing AI response',
    insolvencyPractitioners: 'Error parsing AI response',
    lawFirm: 'Error parsing AI response',
    rawResponse: aiResponse.choices[0].message.content
  };
}

const now = new Date().toISOString();

return [{
  json: {
    // Original case data
    causeNumber: caseData.causeNumber,
    filingDate: caseData.filingDate,
    title: caseData.title,
    subject: caseData.subject,
    register: caseData.register,
    scrapedAt: caseData.scrapedAt,
    pdfUrl: caseData.pdfUrl,
    boxFileId: caseData.boxFileId,

    // AI extracted data
    partyDetails: extractedData.partyDetails || 'Not specified in document',
    timelineOfEvents: extractedData.timeline || 'Not specified in document',
    financialStatus: extractedData.financialStatus || 'Not specified in document',
    insolvencyPractitioners: extractedData.insolvencyPractitioners || 'Not specified in document',
    lawFirm: extractedData.lawFirm || 'Not specified in document',

    // Processing metadata
    analyzedAt: now,
    analysisModel: 'gpt-4o-vision',
    pdfSize: caseData.pdfSize || 0
  }
}];

// Aggregate all processed cases for reporting
const processedCases = $items("Parse AI Analysis Results").map(i => i.json);

if (processedCases.length === 0) {
  return [{ json: { report: "No new cases to report today." } }];
}

// Build report header
const header = `ðŸ“‹ Cayman Islands Registry Report - ${new Date().toLocaleDateString()}\n\n`;

// Build case summaries
const caseSummaries = processedCases.map((caseData, index) => {
  const caseNum = `**Case ${index + 1}: ${caseData.causeNumber}**
`;
  const details = `â€¢ **Title:** ${caseData.title}
â€¢ **Filed:** ${caseData.filingDate}
â€¢ **Subject:** ${caseData.subject}
`;
  const parties = `â€¢ **Parties:** ${caseData.partyDetails?.substring(0, 200)}${caseData.partyDetails?.length > 200 ? '...' : ''}`;
  const financial = caseData.financialStatus !== 'Not specified in document' ? `\nâ€¢ **Financial Status:** ${caseData.financialStatus?.substring(0, 150)}${caseData.financialStatus?.length > 150 ? '...' : ''}` : '';
  const lawFirm = caseData.lawFirm !== 'Not specified in document' ? `\nâ€¢ **Law Firm:** ${caseData.lawFirm}` : '';

  return `${caseNum}${details}${parties}${financial}${lawFirm}\n\n`;
});

const summary = `**Summary:** ${processedCases.length} new case${processedCases.length !== 1 ? 's' : ''} processed and analyzed.\n\n`;
const footer = `---\n*Automated report generated by Cayman Registry Monitor*`;

return [{ json: { report: header + caseSummaries.join('') + summary + footer } }];
