// Determine gazette type based on current date
const today = new Date();
const dayOfWeek = today.getDay(); // 0 = Sunday, 5 = Friday
const dayOfMonth = today.getDate();

let gazetteType, gazetteUrl, schedule;

// Friday = check extraordinary gazette
if (dayOfWeek === 5) {
  gazetteType = 'Extraordinary Gazette';
  gazetteUrl = 'https://www.gov.ky/gazettes/extraordinary-gazettes';
  schedule = 'Every Friday';
} else if (dayOfMonth === 1 || dayOfMonth === 15) {
  // Check regular gazette every 2 weeks (1st and 15th)
  gazetteType = 'Regular Gazette';
  gazetteUrl = 'https://www.gov.ky/gazettes/gazettes';
  schedule = 'Every 2 weeks';
} else {
  // Default to extraordinary for testing
  gazetteType = 'Extraordinary Gazette';
  gazetteUrl = 'https://www.gov.ky/gazettes/extraordinary-gazettes';
  schedule = 'Test Mode';
}

return [{
  json: {
    gazetteType: gazetteType,
    gazetteUrl: gazetteUrl,
    schedule: schedule,
    date: today.toISOString().split('T')[0]
  }
}];

