// Determine which gazettes to check based on current date
const today = new Date();
const dayOfWeek = today.getDay(); // 0 = Sunday, 5 = Friday
const dayOfMonth = today.getDate();

const gazettes = [];

// Friday = check extraordinary gazette
if (dayOfWeek === 5) {
  gazettes.push({
    type: "Extraordinary Gazette",
    url: "https://www.gov.ky/gazettes/extraordinary-gazettes",
    schedule: "Every Friday",
    date: today.toISOString().split("T")[0]
  });
}

// Check regular gazette every 2 weeks (1st and 15th)
if (dayOfMonth === 1 || dayOfMonth === 15) {
  gazettes.push({
    type: "Regular Gazette", 
    url: "https://www.gov.ky/gazettes/gazettes",
    schedule: "Every 2 weeks",
    date: today.toISOString().split("T")[0]
  });
}

// If no gazettes scheduled for today, run test mode
if (gazettes.length === 0) {
  gazettes.push({
    type: "Test Run",
    url: "https://www.gov.ky/gazettes/extraordinary-gazettes",
    schedule: "Test Mode",
    date: today.toISOString().split("T")[0]
  });
}

return gazettes.map(gazette => ({ json: gazette }));

// Extract PDF download links from the gazette page
const html = $json || '';
const gazetteType = $items('Determine Gazette Type')[0].json.type;

// Look for PDF links in the HTML
const pdfLinkRegex = /href="([^"]*\.pdf[^"]*)"/gi;
const matches = [...html.matchAll(pdfLinkRegex)];

// Get the most recent PDF (usually the first one)
const pdfUrl = matches.length > 0 ? matches[0][1] : null;

// If relative URL, make it absolute
const fullPdfUrl = pdfUrl && pdfUrl.startsWith('/') 
  ? `https://www.gov.ky${pdfUrl}` 
  : pdfUrl;

// For testing, use a mock PDF URL if none found
const testPdfUrl = 'https://www.gov.ky/gazettes/sample-gazette.pdf';

return [{
  json: {
    gazetteType: gazetteType,
    pdfUrl: fullPdfUrl || testPdfUrl,
    schedule: $items('Determine Gazette Type')[0].json.schedule,
    date: $items('Determine Gazette Type')[0].json.date,
    foundPdf: !!fullPdfUrl
  }
}];

// Filter for Voluntary Liquidator and Creditor Notices section
// PDF4.me returns text in $json.text
const pdfText = $json.text || '';
const gazetteType = $items('Extract PDF Link')[0].json.gazetteType;
const pdfUrl = $items('Extract PDF Link')[0].json.pdfUrl;
const schedule = $items('Extract PDF Link')[0].json.schedule;
const date = $items('Extract PDF Link')[0].json.date;

// Find the start and end of the liquidation section
const liquidationStartRegex = /Voluntary Liquidator and Creditor Notices/i;
const liquidationEndRegex = /(Other Notices|End of Notices|\n\n\n|$)/i;

const startMatch = pdfText.match(liquidationStartRegex);
const endMatch = pdfText.match(liquidationEndRegex);

let liquidationSection = '';

if (startMatch && endMatch) {
  const startIndex = startMatch.index + startMatch[0].length;
  const endIndex = endMatch.index;
  liquidationSection = pdfText.substring(startIndex, endIndex).trim();
} else if (startMatch) {
  // If no end found, take everything after the start
  const startIndex = startMatch.index + startMatch[0].length;
  liquidationSection = pdfText.substring(startIndex).trim();
} else {
  // If no section found, use the whole text (for testing)
  liquidationSection = pdfText;
}

return [{
  json: {
    liquidationSection: liquidationSection,
    gazetteType: gazetteType,
    pdfUrl: pdfUrl,
    schedule: schedule,
    date: date,
    sectionFound: !!startMatch,
    totalTextLength: pdfText.length
  }
}];

// Parse company details from the liquidation section
const liquidationSection = $json.liquidationSection || '';
const gazetteType = $json.gazetteType;

// Split into individual company entries
// Look for patterns like company names followed by details
const companyEntries = [];

// Mock parsing - replace with actual regex patterns
// This is where you'd implement the specific parsing logic for Cayman gazettes
if (gazetteType === 'Extraordinary Gazette') {
  companyEntries.push({
    companyName: 'ABC Holdings Ltd.',
    appointmentType: 'Voluntary Liquidation',
    appointmentDate: '15th January 2025',
    liquidatorReceiver: 'John Smith, Smith & Associates',
    contactDetails: 'john.smith@example.com, +1-345-123-4567',
    gazetteType: 'Extraordinary Gazette',
    gazetteUrl: $json.pdfUrl
  });

  companyEntries.push({
    companyName: 'XYZ Trading Co.',
    appointmentType: 'Receivership',
    appointmentDate: '20th January 2025',
    liquidatorReceiver: 'Jane Doe, Doe Legal Services',
    contactDetails: 'jane.doe@example.com, +1-345-987-6543',
    gazetteType: 'Extraordinary Gazette',
    gazetteUrl: $json.pdfUrl
  });
} else if (gazetteType === 'Regular Gazette') {
  companyEntries.push({
    companyName: 'DEF Industries Ltd.',
    appointmentType: 'Voluntary Liquidation',
    appointmentDate: '1st February 2025',
    liquidatorReceiver: 'Mike Johnson, Johnson & Partners',
    contactDetails: 'mike.johnson@example.com, +1-345-555-0123',
    gazetteType: 'Regular Gazette',
    gazetteUrl: $json.pdfUrl
  });
} else {
  // Test mode
  companyEntries.push({
    companyName: 'TEST Company Ltd.',
    appointmentType: 'Test Liquidation',
    appointmentDate: 'Test Date',
    liquidatorReceiver: 'Test Liquidator',
    contactDetails: 'test@example.com',
    gazetteType: 'Test Run',
    gazetteUrl: $json.pdfUrl
  });
}

// Add processing timestamps
const now = new Date().toISOString();
const processedCompanies = companyEntries.map(company => ({
  ...company,
  processedAt: now,
  addedAt: now,
  source: 'PDF Gazette Scraper'
}));

// Return one item per company
return processedCompanies.map(company => ({ json: company }));

// Filter out duplicate companies
const scrapedCompanies = $items('Parse Company Details').map(i => i.json);
const existingRows = $items('Get Existing Data').map(i => i.json).filter(Boolean);

// Build a set of existing keys (companyName + appointmentDate)
const seen = new Set(
  existingRows
    .map(r => `${String(r.companyName || '').trim().toUpperCase()}_${String(r.appointmentDate || '').trim()}`)
    .filter(Boolean)
);

// Keep only truly new companies
const freshCompanies = scrapedCompanies.filter(company => {
  const key = `${String(company.companyName || '').trim().toUpperCase()}_${String(company.appointmentDate || '').trim()}`;
  return key && !seen.has(key);
});

// If sheet is empty, pass everything through
const out = (freshCompanies.length === 0 && seen.size === 0) ? scrapedCompanies : freshCompanies;

return out.map(company => ({ json: company }));

// Format companies for report
const companies = $input.all().map(i => i.json);

const lines = companies.map((company, idx) => {
  return `${idx + 1}) ${company.companyName} (${company.appointmentDate})
   • Type: ${company.appointmentType}
   • Liquidator/Receiver: ${company.liquidatorReceiver}
   • Contact: ${company.contactDetails}
   • Gazette: ${company.gazetteType}`;
});

return [{ json: { lines, companies } }];

const { lines = [], companies = [] } = $input.item.json;

const header = '📰 Cayman Gazette – New Liquidations\n';
const footer = '\n— End of gazette update —';
const body = lines.length ? lines.join('\n\n') : 'No new liquidations found today.';

// Create summary
const summary = companies.length > 0 ? 
  `\n\n📊 Summary: Found ${companies.length} new liquidation(s) in ${companies[0]?.gazetteType || 'gazette'}` : 
  '\n\n📊 Summary: No new liquidations found';

return [{ json: { 
  report: `${header}${body}${summary}${footer}`,
  companyCount: companies.length,
  gazetteType: companies[0]?.gazetteType || 'Unknown'
} }];
