// Determine which gazettes to check based on current date
const today = new Date();
const dayOfWeek = today.getDay(); // 0 = Sunday, 5 = Friday
const dayOfMonth = today.getDate();

const gazettes = [];

// Friday = check extraordinary gazette
if (dayOfWeek === 5) {
  gazettes.push({
    type: "Extraordinary Gazette",
    url: "https://www.gov.ky/gazettes/extraordinary-gazettes",
    schedule: "Every Friday",
    date: today.toISOString().split("T")[0],
    scrapeUrl: "https://www.gov.ky/gazettes/extraordinary-gazettes"
  });
}

// Check regular gazette every 2 weeks (1st and 15th)
if (dayOfMonth === 1 || dayOfMonth === 15) {
  gazettes.push({
    type: "Regular Gazette", 
    url: "https://www.gov.ky/gazettes/gazettes",
    schedule: "Every 2 weeks",
    date: today.toISOString().split("T")[0],
    scrapeUrl: "https://www.gov.ky/gazettes/gazettes"
  });
}

// If no gazettes scheduled for today, run test mode
if (gazettes.length === 0) {
  gazettes.push({
    type: "Test Run",
    url: "https://www.gov.ky/gazettes/extraordinary-gazettes",
    schedule: "Test Mode",
    date: today.toISOString().split("T")[0],
    scrapeUrl: "https://www.gov.ky/gazettes/extraordinary-gazettes"
  });
}

return gazettes.map(gazette => ({ json: gazette }));

// Extract PDF download links from the gazette page with error handling
const html = $json || '';
const gazetteType = $items('Determine Gazette Type')[0].json.type;

// Look for PDF links in the HTML with multiple patterns
const pdfLinkPatterns = [
  /href="([^"]*\.pdf[^"]*)"/gi,
  /href='([^']*\.pdf[^']*)'/gi,
  /"([^"]*\.pdf[^"]*)"/gi
];

let pdfUrl = null;
let foundPdf = false;

// Try each pattern
for (const pattern of pdfLinkPatterns) {
  const matches = [...html.matchAll(pattern)];
  if (matches.length > 0) {
    pdfUrl = matches[0][1];
    foundPdf = true;
    break;
  }
}

// If relative URL, make it absolute
if (pdfUrl && pdfUrl.startsWith('/')) {
  pdfUrl = `https://www.gov.ky${pdfUrl}`;
} else if (pdfUrl && !pdfUrl.startsWith('http')) {
  pdfUrl = `https://www.gov.ky/${pdfUrl}`;
}

// For testing, use a mock PDF URL if none found
const testPdfUrl = 'https://www.gov.ky/gazettes/sample-gazette.pdf';

return [{
  json: {
    gazetteType: gazetteType,
    pdfUrl: pdfUrl || testPdfUrl,
    schedule: $items('Determine Gazette Type')[0].json.schedule,
    date: $items('Determine Gazette Type')[0].json.date,
    foundPdf: foundPdf,
    htmlLength: html.length,
    error: foundPdf ? null : 'No PDF link found in HTML'
  }
}];

// Filter for Voluntary Liquidator and Creditor Notices section with robust error handling
const pdfText = $json.text || '';
const gazetteType = $items('Extract PDF Link')[0].json.gazetteType;
const pdfUrl = $items('Extract PDF Link')[0].json.pdfUrl;
const schedule = $items('Extract PDF Link')[0].json.schedule;
const date = $items('Extract PDF Link')[0].json.date;

// Multiple patterns to find liquidation section
const liquidationPatterns = [
  /Voluntary Liquidator and Creditor Notices/i,
  /VOLUNTARY LIQUIDATOR AND CREDITOR NOTICES/i,
  /Liquidation Notices/i,
  /LIQUIDATION NOTICES/i,
  /Creditor Notices/i,
  /CREDITOR NOTICES/i
];

const endPatterns = [
  /Other Notices/i,
  /OTHER NOTICES/i,
  /End of Notices/i,
  /END OF NOTICES/i,
  /\n\n\n/g,
  /\n\n\n\n/g
];

let liquidationSection = '';
let sectionFound = false;
let startMatch = null;
let endMatch = null;

// Try each pattern to find the start
for (const pattern of liquidationPatterns) {
  startMatch = pdfText.match(pattern);
  if (startMatch) {
    sectionFound = true;
    break;
  }
}

// Try each pattern to find the end
if (sectionFound) {
  for (const pattern of endPatterns) {
    endMatch = pdfText.match(pattern);
    if (endMatch) {
      break;
    }
  }
}

// Extract the section
if (sectionFound) {
  const startIndex = startMatch.index + startMatch[0].length;
  const endIndex = endMatch ? endMatch.index : pdfText.length;
  liquidationSection = pdfText.substring(startIndex, endIndex).trim();
} else {
  // If no section found, use the whole text (for testing)
  liquidationSection = pdfText;
}

return [{
  json: {
    liquidationSection: liquidationSection,
    gazetteType: gazetteType,
    pdfUrl: pdfUrl,
    schedule: schedule,
    date: date,
    sectionFound: sectionFound,
    totalTextLength: pdfText.length,
    sectionLength: liquidationSection.length,
    error: sectionFound ? null : 'No liquidation section found'
  }
}];

